import{_ as e,r as a,o as r,c as o,a as l,e as n,F as t,b as h,f as s}from"./app.9fcaaf32.js";const d={},c=l("h2",{id:"\u7ECF\u6587",tabindex:"-1"},[l("a",{class:"header-anchor",href:"#\u7ECF\u6587","aria-hidden":"true"},"#"),h(" \u7ECF\u6587")],-1),_={href:"https://www.biblegateway.com/passage/?search=Genesis+21%3A8-21%3BPsalm+86%3A1-10%2C+16-17%3BJeremiah+20%3A7-13%3BPsalm+69%3A7-10%2C+%2811-15%29%2C+16-1%3BRomans+6%3A1b-11%3BMatthew+10%3A24-39&version=CUVMPS",target:"_blank",rel:"noopener noreferrer"},b=l("strong",null,"\u521B\u4E16\u8BB0 21:8-21:21, \u8BD7\u7BC7 86:1-86:10, \u8BD7\u7BC7 86:16-86:17, \u8036\u5229\u7C73\u4E66 20:7-20:13, \u8BD7\u7BC7 69:7-69:10, \u8BD7\u7BC7 69:11-69:15, \u8BD7\u7BC7 69:16, \u7F57\u9A6C\u4E66 6:1-6:11, \u9A6C\u592A\u798F\u97F3 10:24-10:39",-1),f=s('<h2 id="\u95EE\u9898\u5F15\u5BFC" tabindex="-1"><a class="header-anchor" href="#\u95EE\u9898\u5F15\u5BFC" aria-hidden="true">#</a> \u95EE\u9898\u5F15\u5BFC</h2><h3 id="\u521B\u4E16\u8BB0-21-8-21-21" tabindex="-1"><a class="header-anchor" href="#\u521B\u4E16\u8BB0-21-8-21-21" aria-hidden="true">#</a> \u521B\u4E16\u8BB0 21:8-21:21</h3><ol><li>\u4EC0\u4E48\u4F7F\u4E9A\u4F2F\u62C9\u7F55\u5C06\u590F\u7532\u548C\u513F\u5B50\u9001\u8D70?</li><li>\u4E3A\u4EC0\u4E48\u4EE5\u6492\u8981\u903C\u8FEB\u590F\u52A0\u548C\u4EE5\u5B9E\u739B\u5229\u79BB\u5F00?\u5979\u662F\u6000\u7740\u4EC0\u4E48\u5FC3\u60C5\u8FD9\u4E48\u505A\u7684?</li><li>\u795E\u5982\u4F55\u5E94\u5141\u548C\u4FDD\u62A4\u590F\u7532?</li><li>\u590F\u7532\u5728\u65F7\u91CE\u4F4F\u5728\u4EC0\u4E48\u5730\u65B9?\u5979\u548C\u513F\u5B50\u90FD\u662F\u5982\u4F55\u751F\u5B58\u7684?</li></ol><h3 id="\u8BD7\u7BC7-86-1-86-10-\u8BD7\u7BC7-86-16-86-17" tabindex="-1"><a class="header-anchor" href="#\u8BD7\u7BC7-86-1-86-10-\u8BD7\u7BC7-86-16-86-17" aria-hidden="true">#</a> \u8BD7\u7BC7 86:1-86:10 ,\u8BD7\u7BC7 86:16-86:17</h3><ol><li>\u795E\u6709\u4EC0\u4E48\u5C5E\u6027\u4F7F\u5F97\u8BD7\u4EBA\u575A\u4FE1\u795E\u5FC5\u5B9A\u4FEF\u542C\u4ED6\u7684\u7977\u544A?</li><li>\u8BD7\u4EBA\u5982\u4F55\u63CF\u8FF0\u81EA\u5DF1?\u4ED6\u53C8\u600E\u6837\u6C42\u544A\u795E?</li><li><strong>\u8BD7\u4EBA\u5728\u8BD7\u7BC786 \u4E2D\u8868\u8FBE\u4E86\u4EC0\u4E48\u6837\u7684\u5FC3\u60C5?\u4ED6\u5BF9\u4E0A\u5E1D\u6709\u4EC0\u4E48\u6837\u7684\u6E34\u6C42?</strong></li></ol><h3 id="\u8036\u5229\u7C73\u4E66-20-7-20-13" tabindex="-1"><a class="header-anchor" href="#\u8036\u5229\u7C73\u4E66-20-7-20-13" aria-hidden="true">#</a> \u8036\u5229\u7C73\u4E66 20:7-20:13</h3><ol><li>\u8036\u5229\u7C73\u8D77\u521D\u4E3A\u4EC0\u4E48\u8981\u4F20\u8BB2\u795E\u7684\u8BDD?\u540E\u6765\u4ED6\u6539\u53D8\u4E3B\u610F\u4E86\u5417?<br> \u8036\u5229\u7C73\u66FE\u88AB\u795E\u529D\u5BFC,\u8036\u5229\u7C73\u542C\u4E86\u795E\u7684\u529D\u5BFC,\u56E0\u4E3A\u8036\u5229\u7C73\u8BA4\u4E3A\u795E\u6BD4\u4ED6\u66F4\u6709\u529B\u91CF,\u6240\u4EE5\u8036\u5229\u7C73\u9009\u62E9\u4F20\u8BB2\u795E\u7684\u8BDD,\u5E76\u4E14\u4E00\u70B9\u90FD\u4E0D\u540E\u6094,\u56E0\u4E3A\u82E5\u4E0D\u4F20\u8BB2\u795E,\u5FC3\u91CC\u4FBF\u5C40\u7684\u6709\u706B\u5728\u70E7,\u6240\u4EE5\u60C5\u4E0D\u81EA\u7981\u3002</li><li>\u4EBA\u4EEC\u5982\u4F55\u8FEB\u5BB3\u8036\u5229\u7C73?\u8FD9\u4F7F\u4ED6\u611F\u5230\u6CAE\u4E27\u5417? <ol><li>\u7EC8\u65E5\u6210\u4E3A\u7B11\u8BDD,\u4EBA\u4EBA\u90FD\u620F\u5F04</li><li>\u88AB\u51CC\u8FB1\u3001\u8BA5\u523A</li><li>\u8BB8\u591A\u4EBA\u7684\u8C17\u8C24\uFF0C\u56DB\u56F4\u90FD\u662F\u60CA\u5413</li><li>\u77E5\u5DF1\u597D\u53CB\u4E5F\u7AA5\u63A2,\u5E0C\u671B\u4ED6\u8DCC\u5012</li></ol></li><li>\u8036\u5229\u7C73\u8BF4\u4ED6\u5FCD\u53D7\u8FD9\u4E9B\u82E6\u96BE\u7684\u539F\u56E0\u662F\u4EC0\u4E48? <ol><li>\u8036\u548C\u534E\u4E0E\u4ED6\u540C\u5728,\u5FC5\u4F7F\u654C\u4EBA\u5927\u5927\u8499\u7F9E</li></ol></li><li>**\u8036\u5229\u7C73\u4E3A\u4EC0\u4E48\u611F\u5230\u5982\u6B64\u75DB\u82E6\u548C\u6124\u6012?\u4ED6\u8BD5\u56FE\u6297\u62D2\u4E0A\u5E1D\u7684\u8BDD\u5374\u5931\u8D25\u4E86\u5417? **</li></ol><h3 id="\u8BD7\u7BC7-69-7-69-18" tabindex="-1"><a class="header-anchor" href="#\u8BD7\u7BC7-69-7-69-18" aria-hidden="true">#</a> \u8BD7\u7BC7 69:7-69:18</h3><ol><li>\u8BD7\u4EBA\u56E0\u4EC0\u4E48\u53D7\u5230\u7F9E\u8FB1\u548C\u9694\u79BB?</li><li>\u4ED6\u5F62\u5BB9\u81EA\u5DF1\u7684\u5904\u5883\u6709\u591A\u7CDF\u7CD5? <ol><li>\u5F1F\u5144\u770B\u6211\u4E3A\u5916\u8DEF\u4EBA\uFF0C\u6211\u7684\u540C\u80DE\u770B\u6211\u4E3A\u5916\u90A6\u4EBA</li><li>\u9EBB\u5E03\u5F53\u8863\u88F3\uFF0C\u5C31\u6210\u4E86\u4ED6\u4EEC\u7684\u7B11\u8C08</li></ol></li><li>\u8BD7\u4EBA\u5728\u8FD9\u6BB5\u7ECF\u6587\u4E2D\u5411\u795E\u63D0\u51FA\u4EC0\u4E48\u8BF7\u6C42? <ol><li>\u642D\u6551\u6211\u51FA\u79BB\u6DE4\u6CE5</li><li>\u8131\u79BB\u90A3\u4E9B\u6068\u6211\u7684\u4EBA</li><li>\u4E0D\u5BB9\u5927\u6C34\u6F2B\u8FC7\u6211</li><li>\u4E0D\u5BB9\u6DF1\u6E0A\u541E\u706D\u6211</li><li>\u4E0D\u5BB9\u5751\u574E\u5728\u6211\u4EE5\u4E0A\u5408\u53E3</li><li>\u6C42\u4F60\u4EB2\u8FD1\u6211\uFF0C\u6551\u8D4E\u6211\uFF0C\u6C42\u4F60\u56E0\u6211\u7684\u4EC7\u654C\u628A\u6211\u8D4E\u56DE</li></ol></li><li>\u4ED6\u4E3A\u4EC0\u4E48\u76F8\u4FE1\u8FD9\u4E9B\u8BF7\u6C42\u80FD\u88AB\u542C\u89C1? <ol><li>\u4E30\u76DB\u7684\u6148\u7231\uFF0C\u62EF\u6551\u7684\u8BDA\u5B9E</li><li>\u56E0\u4E3A\u4F60\u7684\u6148\u7231\u672C\u4E3A\u7F8E\u597D\u3002\u6C42\u4F60\u6309\u4F60\u4E30\u76DB\u7684\u6148\u60B2\uFF0C\u56DE\u8F6C\u7737\u987E\u6211</li></ol></li><li>\u8BD7\u4EBA\u5728\u8FD9\u91CC\u5BF9\u795E\u7684\u6148\u7231\u8868\u73B0\u51FA\u4EC0\u4E48\u6837\u7684\u786E\u4FE1?</li><li><strong>69\u7BC7\u662F\u4E00\u7BC7\u75DB\u82E6\u7684\u54C0\u8BC9,\u8BD7\u4EBA\u5728\u5176\u4E2D\u8868\u8FBE\u4E86\u4EC0\u4E48\u6837\u7684\u60C5\u611F?\u4ED6\u5BF9\u5468\u56F4\u7684\u4EBA\u6709\u4EC0\u4E48\u6837\u7684\u611F\u53D7?</strong></li></ol><h3 id="\u7F57\u9A6C\u4E66-6-1-6-11" tabindex="-1"><a class="header-anchor" href="#\u7F57\u9A6C\u4E66-6-1-6-11" aria-hidden="true">#</a> \u7F57\u9A6C\u4E66 6:1-6:11</h3><ol><li>\u4FE1\u5F92\u4E0E\u57FA\u7763\u8054\u5408\u540E\u5BF9\u7F6A\u7684\u5173\u7CFB\u53D1\u751F\u4E86\u4EC0\u4E48\u53D8\u5316? <ol><li>\u5728\u7F6A\u4E0A\u6B7B\u4E86,\u4F7F\u7F6A\u8EAB\u706D\u7EDD\uFF0C\u53EB\u6211\u4EEC\u4E0D\u518D\u505A\u7F6A\u7684\u5974\u4EC6,\u8131\u79BB\u4E86\u7F6A</li></ol></li><li>\u4FE1\u5F92\u5E94\u8BE5\u600E\u6837\u6B7B\u4E8E\u7F6A,\u6D3B\u7ED9\u795E? <ol><li>\u4E0D\u53EF\u4ECD\u5728\u7F6A\u4E2D\u6D3B\u7740</li><li>\u4E00\u4E3E\u4E00\u52A8\u6709\u65B0\u751F\u7684\u6837\u5F0F</li></ol></li><li>\u57FA\u7763\u7684\u6B7B\u5982\u4F55\u4F7F\u4FE1\u5F92\u5F97\u4EE5\u8131\u79BB\u7F6A\u7684\u8F96\u5236? <ol><li>\u6211\u4EEC\u7684\u65E7\u4EBA\u548C\u4ED6\u540C\u9489\u5341\u5B57\u67B6,\u4E0E\u57FA\u7763\u540C\u6B7B\uFF0C\u53C8\u4E0E\u4ED6\u540C\u6D3B,</li><li>\u7763\u65E2\u4ECE\u6B7B\u91CC\u590D\u6D3B\uFF0C\u5C31\u4E0D\u518D\u6B7B\uFF0C\u6B7B\u4E5F\u4E0D\u518D\u505A\u4ED6\u7684\u4E3B\u4E86\u3002</li><li>\u4ED6\u6B7B\u662F\u5411\u7F6A\u6B7B\u4E86\uFF0C\u53EA\u6709\u4E00\u6B21\uFF1B\u4ED6\u6D3B\u662F\u5411\u795E\u6D3B\u7740\u3002</li></ol></li><li><strong>\u7F57\u9A6C\u4E66\u7B2C6\u7AE0\u5982\u4F55\u89E3\u91CA\u6211\u4EEC\u901A\u8FC7\u4FE1\u4EF0\u8036\u7A23\u57FA\u7763\u6B7B\u800C\u4E0E\u7F6A\u8131\u79BB\u7684\u5173\u7CFB?</strong></li></ol><h3 id="\u9A6C\u592A\u798F\u97F3-10-24-10-39" tabindex="-1"><a class="header-anchor" href="#\u9A6C\u592A\u798F\u97F3-10-24-10-39" aria-hidden="true">#</a> \u9A6C\u592A\u798F\u97F3 10:24-10:39</h3><ol><li>\u95E8\u5F92\u4E0D\u5E94\u8BE5\u4E3A\u4EC0\u4E48\u611F\u5230\u60E7\u6015?\u4E3B\u4EBA\u548C\u4EC6\u4EBA\u7684\u5730\u4F4D\u6709\u4F55\u5173\u7CFB? <ol><li>\u4E0D\u5E94\u8BE5\u56E0\u4E3A\u88AB\u4EBA\u8FB1\u9A82\u903C\u8FEB\u800C\u60E7\u6015,\u95E8\u5F92\u4F5C\u4E3A\u5B66\u751F\u6240\u8DDF\u968F\u7684\u8036\u7A23\u90FD\u88AB\u4EBA\u9A82\u662F\u522B\u897F\u535C,\u66F4\u4F55\u51B5\u662F\u95E8\u5F92\u4EEC\u5462?</li><li>\u76D6\u7684\u4E8B\u6CA1\u6709\u4E0D\u9732\u51FA\u6765\u7684\uFF0C\u9690\u85CF\u7684\u4E8B\u6CA1\u6709\u4E0D\u88AB\u4EBA\u77E5\u9053\u7684</li><li>\u4ED6\u4EEC\u53EA\u80FD\u6740\u8EAB\u4F53,\u800C\u7075\u9B42\u4E0E\u8EAB\u4F53\u90FD\u662F\u795E\u5728\u638C\u7BA1,\u6CA1\u6709\u795E\u7684\u5141\u8BB8,\u4EBA\u5E76\u4E0D\u80FD\u4F24\u5BB3\u95E8\u5F92</li></ol></li><li>\u4F20\u798F\u97F3\u548C\u627F\u8BA4\u4E3B\u4F1A\u5E26\u6765\u4EC0\u4E48\u7ED3\u679C? <ol><li>\u5728\u5730\u4E0A\u88AB\u52A8\u5200\u5175</li><li>\u4EB2\u4EBA\u751F\u758F</li></ol></li><li>\u4EC0\u4E48\u662F\u81F3\u5173\u91CD\u8981\u548C\u4E0D\u53EF\u8BA1\u4EF7\u7684?\u4E3A\u4EC0\u4E48? <ol><li>\u7231\u795E\u8FC7\u4E8E\u5176\u4ED6</li><li>\u80CC\u7740\u81EA\u5DF1\u7684\u5341\u5B57\u67B6\u8DDF\u4ECE\u4E3B</li></ol></li><li><strong>\u9A6C\u592A10:24-39 \u5982\u4F55\u8BBA\u8FF0\u95E8\u5F92\u8981\u9884\u5907\u9762\u5BF9\u7684\u903C\u8FEB\u4E0E\u82E6\u96BE?</strong></li></ol><p>\u7ECF\u6587\u8868\u8FBE\u4E86\u4EBA\u5728\u9762\u5BF9\u56F0\u96BE\u3001\u75DB\u82E6\u548C\u903C\u8FEB\u65F6\u7684\u5404\u79CD\u60C5\u611F\u3002\u4F46\u662F\u4E5F\u540C\u65F6\u5F3A\u8C03\u4E86\u4E0A\u5E1D\u7684\u6148\u7231,\u4FDD\u62A4\u548C\u62EF\u6551\u3002\u4E0A\u5E1D\u59CB\u7EC8\u662F\u53EF\u4FE1\u9760\u7684\u907F\u96BE\u6240\u3002\u901A\u8FC7\u4FE1\u4EF0\u6211\u4EEC\u53EF\u4EE5\u4E0E\u7F6A\u7684\u8F96\u5236\u8131\u79BB,\u83B7\u5F97\u771F\u6B63\u81EA\u7531\u7684\u751F\u547D\u3002\u4F5C\u4E3A\u95E8\u5F92,\u6211\u4EEC\u9700\u8981\u9884\u5907\u597D\u9762\u5BF9\u56E0\u4E3A\u8DDF\u968F\u8036\u7A23\u800C\u6765\u7684\u5404\u6837\u8270\u96BE,\u4F46\u6211\u4EEC\u7684\u751F\u547D\u5C5E\u4E8E\u57FA\u7763,\u6211\u4EEC\u80FD\u575A\u6301\u5230\u5E95\u3002</p><h2 id="\u6574\u4F53\u8109\u7EDC" tabindex="-1"><a class="header-anchor" href="#\u6574\u4F53\u8109\u7EDC" aria-hidden="true">#</a> \u6574\u4F53\u8109\u7EDC</h2><p>I. \u5F15\u8A00:\u6211\u4EEC\u7684\u751F\u547D\u4E2D\u4F1A\u9047\u5230\u5404\u79CD\u78E8\u96BE\u548C\u56F0\u82E6,\u4F46\u6211\u4EEC\u6709\u4E00\u4E2A\u80FD\u5E2E\u52A9\u6211\u4EEC\u7684\u795E\u3002</p><p>II. \u4E0A\u5E1D\u4F1A\u542C\u6211\u4EEC\u7684\u547C\u6C42\u5E76\u62EF\u6551\u6211\u4EEC(\u521B21:8-21, \u8BD786:1-10,86:16-17)<br> \xA0\xA0\xA0\xA0\xA0A. \u4EE5\u5B9E\u739B\u5229\u88AB\u9057\u5F03\u5728\u65F7\u91CE,\u795E\u542C\u5230\u4ED6\u7684\u54ED\u58F0\u5E76\u62EF\u6551\u4ED6\u3002\u795E\u4E5F\u4F1A\u542C\u6211\u4EEC\u7684\u547C\u6551\u3002<br> \xA0\xA0\xA0\xA0\xA0B. \u8BD7\u4EBA\u547C\u6C42\u795E\u7684\u6069\u5178\u548C\u601C\u60AF\u3002\u795E\u662F\u6709\u601C\u60AF,\u6709\u6069\u5178,\u4E0D\u8F7B\u6613\u53D1\u6012,\u5E76\u5927\u6709\u6148\u7231\u601C\u60AF\u7684\u795E\u3002\u4ED6\u5FC5\u542C\u6211\u4EEC\u7684\u7948\u6C42\u3002 \xA0\xA0\xA0\xA0\xA0 III. \u6211\u4EEC\u4F1A\u53D7\u5230\u903C\u8FEB\u548C\u53D7\u82E6,\u4F46\u6211\u4EEC\u53EF\u4EE5\u5BFB\u6C42\u795E\u7684\u5E2E\u52A9(\u803620:7-13, \u8BD769:7-18)<br> \xA0\xA0\xA0\xA0\xA0A. \u8036\u5229\u7C73\u9762\u4E34\u903C\u8FEB,\u4F46\u4ED6\u9009\u62E9\u5BFB\u6C42\u795E\u5E76\u628A\u81EA\u5DF1\u7684\u75DB\u82E6\u544A\u8BC9\u795E\u3002<br> \xA0\xA0\xA0\xA0\xA0B. \u8BD7\u4EBA\u5728 69 \u7BC7\u54C0\u544A\u795E,\u8BA4\u51FA\u81EA\u5DF1\u53D7\u7684\u82E6\u662F\u4E3A\u795E\u7684\u7F18\u6545\u3002\u4ED6\u6C42\u795E\u4E0D\u8981\u4F7F\u4ED6\u906D\u53D7\u90A3\u4E9B\u6068\u4ED6\u7684\u4EBA\u6240\u89C4\u5212\u7684\u707E\u7978\u3002</p><p>IV.\u6211\u4EEC\u4FE1\u4E3B\u540E\u8981\u6D3B\u5728\u65B0\u751F\u547D\u91CC,\u4E0D\u518D\u4F5C\u7F6A\u7684\u5974\u4EC6(\u7F576:1-11)<br> \xA0\xA0\xA0\xA0\xA0A. \u6211\u4EEC\u85C9\u7740\u6D17\u793C,\u4E0E\u4E3B\u4E00\u540C\u57CB\u846C,\u4E0E\u4E3B\u4E00\u540C\u590D\u6D3B,\u80FD\u6D3B\u5728\u65B0\u751F\u547D\u91CC\u3002<br> \xA0\xA0\xA0\xA0\xA0B. \u6211\u4EEC\u8981\u8BA4\u5B9A\u81EA\u5DF1\u662F\u6B7B\u4E86\u7684,\u5C31\u4E0D\u5728\u53D7\u7F6A\u7684\u5236\u7EA6,\u8981\u6D3B\u7740\u4E8B\u5949\u795E\u3002V. \u7ED3\u8BBA:\u867D\u7136\u6211\u4EEC\u5728\u4E16\u4E0A\u4F1A\u6709\u82E6\u96BE,\u4F46\u6211\u4EEC\u53EF\u4EE5\u653E\u5FC3\u5730\u8DDF\u968F\u4E3B,\u56E0\u4E3A\u4E3B\u4F1A\u4E0E\u6211\u4EEC\u540C\u5728\u3002\u6211\u4EEC\u8981\u50CF\u4E3B\u8036\u7A23\u4E00\u6837,\u4E3A\u4E3B\u7684\u7F18\u6545\u7518\u613F\u53D7\u82E6\u3002\u4E3B\u4F1A\u8D4F\u8D50\u90A3\u4E9B\u4E3A\u4ED6\u5931\u53BB\u751F\u547D\u7684\u4EBA\u3002(\u592A10:24-39)</p>',18);function p(u,x){const i=a("ExternalLinkIcon");return r(),o(t,null,[c,l("p",null,[l("a",_,[b,n(i)])]),f],64)}var g=e(d,[["render",p],["__file","2023-06-25.html.vue"]]);export{g as default};
